import React, { Component } from 'react';
import {fetchAPI} from './CallAPIService.js';
import {Link} from "react-router-dom";

class Counter extends Component { 
    state = {
        APImessage: '',
        userinput: ''
        
    };
    buttonClicked =() =>{
        console.log("click");
        //this.setState({userinput:});
        fetchAPI(window.location.pathname,this.state.userinput).then(response => this.setState({APImessage: response}));
        
        
    }
    
    render() { 
        return <div className='div'>
            <div>
            <h1>CloudDrive</h1>
            <span>{this.state.APImessage}</span>
            </div>
            <div>
            <input type="text" value={this.userinput}/>
            <button 
            onClick={this.buttonClicked}
            className = "btn btn-primary btn-sm">pipo</button>
            </div>
            <div>
            <Link to= "login"><button className = "btn btn-secondary btn-sm">login</button></Link>
            <Link to = "/"><button className = "btn btn-secondary btn-sm">Home</button></Link>
            </div>
           
        </div>;
    }
    
}
 
export default Counter;
