import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios  from 'axios';



class UserScreen extends Component {
    state = { 
      selectedFile: null,
      selectedUser: '',
      filesFromDB: []
    } 

    butooClicked =()=>{
      

      fetch("http://localhost:9000/user/download").then(res => res.blob())
      .then(blob => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.style.display = "none";
        a.href = url;
        // the filename you want
        a.download = "download";
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        alert("your file has downloaded!"); // or you know, something with better UX...
      })
      .catch(() => alert("oh no!"));
    }

    handleChange =(event) => {
        
      //this.setState({value: event.target.value});
      this.setState({selectedUser: event.target.value});
    }

    onFileChange = event => {
    
        // Update the state
        this.setState({ selectedFile: event.target.files[0] });
        //this.setState({ selectedUser: })
        //this.onFileUpload();
      
      };

    onFileUpload = () => {
      const formData = new FormData();
    
      console.log(this.state.selectedUser);
      console.log(this.state.selectedFile);
      console.log(this.state.selectedFile.name);
      
      // Update the formData object
      formData.append(
        "myFile",
        //{username: this.state.selectedUser},
        this.state.selectedFile,
        this.state.selectedFile.name
        
      );
    
      // Details of the uploaded file
      console.log(this.state.selectedFile);
      console.log(Array.from(formData)[1]);
    
      // Request made to the backend api
      // Send formData object
      axios.post("http://localhost:9000/user/upload", formData);
    
        // Details of the uploaded file
        //console.log(this.state.selectedFile);
        
        
        
      };

      fileData = () => {
    
        if (this.state.selectedFile) {
           
          return (
            <div>
              <h2>File Details:</h2>
               
  <p>File Name: {this.state.selectedFile.name}</p>
   
               
  <p>File Type: {this.state.selectedFile.type}</p>

  <p>File content: {this.state.selectedFile.fileData}</p>
   
               
  <p>
                Last Modified:{" "}
                {this.state.selectedFile.lastModifiedDate.toDateString()}
              </p>
   
            </div>
          );
        } else {
          return (
            <div>
              <br />
              <h4>Choose before Pressing the Upload button</h4>
            </div>
          );
        }
      };
      
    

    render() { 
        return <div className="App" >
          <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh'}}>
        
        file:<input type="file" name='upload' onChange={this.onFileChange} />
        name:<input type="text" name='name' onChange={this.handleChange} />
        <button className='btn btn-primary btn-sm' onClick={this.onFileUpload}>upload</button>
       </div>
       
        {this.fileData()}
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <button className='btn btn-primary btn-sm' onClick ={this.butooClicked}>Download!</button>
        <Link to = "/"><button className = "btn btn-secondary btn-sm">Home</button></Link>
        
        </div>
      </div>;
    }
}
 
export default UserScreen;
