import React, {Component} from 'react';
import {fetchAPI} from './CallAPIService.js';
import {Link} from "react-router-dom";


class LoginScreen extends Component {
    state = {  

    APImessage: '',
    userName: '',
    userPassword: ''
 } 


    buttonClicked =() =>{
        console.log("click");
        //this.setState({userinput:});
        fetchAPI(window.location.pathname,this.state.userName).then(response => this.setState({APImessage: response}));
        console.log(this.userName);
        console.log(this.userPassword);
        
    }

    handleChangeName =(event) => {
        console.log(event.target.username);
        this.setState({value: event.target.username});
        this.setState({userName: event.target.username});
      }

    handleChangePassword = (event) =>{
        console.log(event.target.password);
        this.setState({value: event.target.password});
        this.setState({userPassword: event.target.password});
    }


    render() { 
        return <div className='div'  >
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <h1>CloudDrive</h1>
        </div>
        
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        
        <span>{this.state.APImessage}</span>
        </div>

        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <label>Username
        <input type="text" name='username' value={this.state.username} onChange={this.handleChangeName}/>
        </label>
        <label>Password
        <input type="password" name='password' value={this.state.password} onChange={this.handleChangePassword}/>
        </label>
        
        </div>
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <button 
        onClick={this.buttonClicked}
        className = "btn btn-primary btn-sm">Log In</button>
        </div>
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        
        <Link to = "/"><button className = "btn btn-secondary btn-sm">Home</button></Link>
        </div>
       
    </div>; ;
    }
}
 
export default LoginScreen;
