import React, {Component} from 'react';
import axios from 'axios';
import {Link} from "react-router-dom";


class LoginScreen extends Component {
    state = {  
    isLogged: false,
    APImessage: '',
    userName: '',
    userPassword: '',
    selectedFile: null

 } 


    buttonClicked =() =>{
        console.log("click");
        
        const requestOptions = {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ username: this.state.userName,
          password: this.state.userPassword })
      };
        fetch('http://localhost:9000'+window.location.pathname, requestOptions).then(res=>res.text()).then((res) => {
          console.log('Success:', res);
          if(res === "true"){
            this.setState({APImessage: "logged in!"});
            this.setState({isLogged: true});
          }
            else{ 
              this.setState({APImessage: "Wrong username or password"});}
          
        })
        .catch((error) => {
          console.error('Error:', error);
         
        });
        
        
    }

    onFileChange = event => {
    
      // Update the state
      console.log("setting file");
      this.setState({ selectedFile: event.target.files[0] });
      
    
    };

    buttonRegister=()=>{
      console.log("click");
        
        const requestOptions = {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ username: this.state.userName,
          password: this.state.userPassword })
      };
        fetch('http://localhost:9000'+window.location.pathname, requestOptions);
    }

    onFileUpload = () => {
      const formData = new FormData();
    //let userApp ={username:this.state.selectedUser}
      //console.log(this.state.selectedUser);
      //console.log(this.state.selectedFile);
      //console.log(this.state.selectedFile.name);
      
      // Update the formData object
      formData.append(
        "myFile",
        this.state.selectedFile,
        this.state.selectedFile.name,
        
        
      );
      let pipo ={
        username: this.state.userName,
        file: this.state.selectedFile
        
      }
      // Details of the uploaded file
      console.log(this.state.selectedFile);
      console.log(pipo);
      //console.log(Array.from(formData)[1]);
    
      // Request made to the backend api
      // Send formData object
      axios.post("http://localhost:9000/user/upload", pipo);
    
        // Details of the uploaded file
        //console.log(this.state.selectedFile);
        
        
        
      };

      fileData = () => {
    
        if (this.state.selectedFile) {
           
          return (
            <div>
              <h2>File Details:</h2>
               
  <p>File Name: {this.state.selectedFile.name}</p>
   
               
  <p>File Type: {this.state.selectedFile.type}</p>

  <p>File content: {this.state.selectedFile.fileData}</p>
   
               
  <p>
                Last Modified:{" "}
                {this.state.selectedFile.lastModifiedDate.toDateString()}
              </p>
   
            </div>
          );
        } else {
          return (
            <div>
              <br />
              <h4>Choose before Pressing the Upload button</h4>
            </div>
          );
        }
      };
      

    handleChange =(event) => {
        
        //this.setState({value: event.target.value});
        this.setState({[event.target.name]: event.target.value});
      }



    render() { 
        return <div className='div'  >
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <h1>CloudDrive</h1>
        </div>
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <span>{this.state.APImessage}</span>
        </div>
        
        
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <label>Username
        <input type="text" name='userName' value={this.state.value} onChange={this.handleChange}/>
        </label>
        
        
        </div>
        <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        
        
      }}>
        
        <label>Password
        <input type="password" name='userPassword' value={this.state.value} onChange={this.handleChange}/>
        </label>
        
        </div>
        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        <button 
        onClick={this.buttonClicked}
        className = "btn btn-primary btn-sm">Log In</button>
        {this.state.isLogged ? <Link to="/user">
        <button className ="btn btn-primary btn-sm">Access</button>
        </Link> : null}
        
        
        </div>

        <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh',
        
      }}>
        
        <Link to = "/"><button className = "btn btn-secondary btn-sm">Home</button></Link>
        <Link to="/register">
        <button className ="btn btn-primary btn-sm">Register</button>
        </Link>
        
        </div>
        {this.state.isLogged ?<div> <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '10vh'}}>
        
        <form className='form' action='http://localhost:9000/user/upload' method='POST' encType='multipart/form-data' >
          
          
        file:<input type="file" name='upload' onChange={this.onFileChange} />

        </form>
        <button className='btn btn-primary btn-sm' onClick={this.onFileUpload}>uploadTest</button>
        </div>
        <div>{this.fileData()}</div>
       </div> : null}
        
    </div>; ;
    }
}
 
export default LoginScreen;
